import React from "react";
import API from "../../utils/API";
import PeopleList from "../PeopleList";

class PeopleCard extends React.Component {
  state = {
    view: "all",
    users: [],
    results: [],
    error: "",
  };

  componentDidMount() {
    this.getRandomUsers();
  }

  // handleInputChange = event => {
  //   this.setState({ search: event.targer.value });
  // };

  getRandomUsers = () => {
    API.search()
      .then((res) => {
        const peopleData = res.data.results;
        let userData = peopleData.user.map((user) => {
          let userFormat = {};
          userFormat["image"] = user.picture.small;
          userFormat["name"] = `${user.name.first} ${user.name.last}`;
          userFormat[
            "location"
          ] = `${user.location.city} ${user.location.state}`;

          return userFormat;
        });
        this.setState({ users: userData });
        this.setState({ results: userData });
      })
      .catch((err) => console.log(err));
  };

  render() {
    return (
      <div>
        <div className="fleax-wrap justify content center">
          {this.state.results.map((user) => (
            <PeopleList
              key={this.state.results.indexOf(user)}
              image={user.image}
              name={user.fullname}
              location={user.location}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default PeopleCard;
