import React from "react";
import API from "../../utils/API";
import PeopleList from "../PeopleList ";
import Search from "../Search";

class PeopleCard extends React.Component {
  state = {
    view: "all",
    person: [],
    allPeople: [],
  };

  componentDidMount() {
    this.getRandomUsers();
  }

  // handleInputChange = event => {
  //   this.setState({ search: event.targer.value });
  // };

  getRandomUsers = () => {
    API.search()
      .then((res) => {
        const peopleData = res.data.results;
        let data = peopleData.map((user) => {
          let userFormat = {};
          userFormat["image"] = user.picture.medium;
          userFormat["name"] = `${user.name.first} ${user.name.last}`;
          userFormat[
            "location"
          ] = `${user.location.city}, ${user.location.state}`;
          userFormat["phone"] = user.cell;
          return userFormat;
        });
        this.setState({ person: data });
        this.setState({ allPeople: data });
      })
      .catch((err) => console.log(err));
  };

  filterLocation = (letter) => {
    if (letter !== "all") {
      let selectedUser = [];
      for (const user of this.state.person) {
        let location = user.location.state;
        if (letter === location) {
          selectedUser.push(user);
        }
      }
      this.setState({ allPeople: selectedUser });
      this.setState({ view: letter });
    }
  };
  handleBtnSubmit = (event) => {
    const letter = event.target.location;
    this.filterLocation(letter);
  };

  render() {
    return (
      <div>
        <Search
          filterLocation={this.filterLocation}
          handleBtnSubmit={this.handleBtnSubmit}
        />
        <div className="d-flex flex-wrap justify-content-center">
          {this.state.allPeople.map((user) => (
            <PeopleList
              id={this.state.allPeople.indexOf(user)}
              key={this.state.allPeople.indexOf(user)}
              image={user.image}
              name={user.name}
              location={user.location}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default PeopleCard;
